const t=JSON.parse('{"key":"v-1219de0e","path":"/%E5%8C%BB%E8%97%8F/%E8%AE%B8%E6%B0%8F%E5%8C%BB%E6%A1%88.html","title":"许氏医案","lang":"zh-CN","frontmatter":{"title":"许氏医案","index":true,"icon":"editor","order":990,"category":["医藏","许氏医案"],"tag":["许恩普","清"],"description":"许氏医案 清 许恩普 《许氏医案》提要 方书所载之症治，与人以规矩；医案所记之验案，示人以活法。吾人习医固宜以《内》、《难》《甲乙》筑其基，仲景及后贤治疗各书致其用，尤宜广阅名家医案，以明变化。不然，墨守成法，执方临病，岂能收覆怀之效哉!《许氏医案》一卷，许恩普先贤逐年之验案也。断证如折狱，处方若用兵，且言简意赅，一目了然，洵为医林之羽翼，后学之楷模。...","head":[["meta",{"property":"og:url","content":"https://ruguoaaa.github.io/xirang/xirang1/%E5%8C%BB%E8%97%8F/%E8%AE%B8%E6%B0%8F%E5%8C%BB%E6%A1%88.html"}],["meta",{"property":"og:site_name","content":"息壤"}],["meta",{"property":"og:title","content":"许氏医案"}],["meta",{"property":"og:description","content":"许氏医案 清 许恩普 《许氏医案》提要 方书所载之症治，与人以规矩；医案所记之验案，示人以活法。吾人习医固宜以《内》、《难》《甲乙》筑其基，仲景及后贤治疗各书致其用，尤宜广阅名家医案，以明变化。不然，墨守成法，执方临病，岂能收覆怀之效哉!《许氏医案》一卷，许恩普先贤逐年之验案也。断证如折狱，处方若用兵，且言简意赅，一目了然，洵为医林之羽翼，后学之楷模。..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-09-08T08:41:05.000Z"}],["meta",{"property":"article:author","content":"Paladin"}],["meta",{"property":"article:tag","content":"许恩普"}],["meta",{"property":"article:tag","content":"清"}],["meta",{"property":"article:modified_time","content":"2023-09-08T08:41:05.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"许氏医案\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2023-09-08T08:41:05.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Paladin\\",\\"url\\":\\"https://ruguoaaa.github.io/xirang\\"}]}"]]},"headers":[],"git":{"createdTime":1694162465000,"updatedTime":1694162465000,"contributors":[{"name":"ruguoaaa","email":"ruguoaaa@163.com","commits":1}]},"readingTime":{"minutes":24.99,"words":7497},"filePathRelative":"医藏/许氏医案.md","localizedDate":"2023年9月8日","autoDesc":true}');export{t as data};
